    public function send_email($recipientEmail,$recipientName,$subject,$body,$altBody){
        
        $mail = new PHPMailer(true);

        try {
            //Server settings
            $mail->isSMTP();                                            //Send using SMTP
            // $mail->Host       = 'mail.rickagauba.com';                     //Set the SMTP server to send through
            $mail->Host       = 'smtp.gmail.com';                     //Set the SMTP server to send through

            $mail->SMTPAuth   = true;                                   //Enable SMTP authentication
            $mail->Username   = 'ratneshkarbhari99@gmail.com';                     //SMTP username
            $mail->Password   = 'biryani@47';                               //SMTP password
            $mail->SMTPSecure = 'ssl';         //Enable TLS encryption; `PHPMailer::ENCRYPTION_SMTPS` encouraged
            $mail->Port       = 465;                                    //TCP port to connect to, use 465 for `PHPMailer::ENCRYPTION_SMTPS` above

            //Recipients
            $mail->setFrom('noreply@rickagauba.com', 'Ricka Gauba Team');
            $mail->addAddress($recipientEmail, $recipientName);     //Add a recipient
            $mail->addReplyTo('noreply@rickagauba.com', 'No Reply');

            //Content
            $mail->isHTML(true);                                  //Set email format to HTML
            $mail->Subject = $subject;
            $mail->Body    = $body;
            $mail->AltBody = $altBody;

            if ($mail->send()) {
                return "mail-sent";
            } else {
                return "mail-not-sent";
            }
            
            
        } catch (Exception $e) {
            echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";
        }


    }


