    public function send_contact_email()
    {
        
        $fullName = $this->request->getPost("full_name");
        $email = $this->request->getPost("email");
        $message = $this->request->getPost("message");

        $body = "
        <h4>New Enquiry from contact form</h4>
        <p>Full Name: ".$fullName." </p>
        <p>Email: ".$email." </p>
        <p>Message: ".$message." </p>
        ";

        $altBody = strip_tags($body);

        $res = $this->send_email("info@rickagauba.com","Ricka Gauba","New Message from contact form",$body,$altBody);

        $pageLoader = new PageLoader();

        if ($res=="mail-sent") {
            $pageLoader->contact("Email sent successfully");

        } else {

            echo "Email not sent";
        }
        


    }
    

