    public function update_account_password()
    {
        $email = $_COOKIE["email"];

        $authModel = new AuthModel();

        $customerData = $authModel->where("email",$email)->first();

        $password = $this->request->getPost("password");

        $newdata = [
            "password" => password_hash($password,PASSWORD_DEFAULT)
        ];

        $authModel = new AuthModel();

        $pwdUpdated = $authModel->update($customerData["id"],$newdata);

        if ($pwdUpdated) {
            
            $session = session();

            $session->set($newdata);   

            echo json_encode(array('result' => "success", "successUrl" => site_url("login")));
            
        } else {
            
            echo json_encode(array('result' => "failure","reason"=>"Password not updated"));
            
        }
    }

    public function product_search()
    {

        $universalSearchQuery = $this->request->getPost("universal-search");
        $productModel = new ProductModel();
        $results = $productModel->like("title",$universalSearchQuery)->findAll();
        $data = array("title"=>"Search Results","products"=>$results);

        $this->public_page_loader("shop",$data);

    }


